import React from 'react';
import '../stylesheets/login.css';
import { loginUser } from '../api/authServlet';
import { UNAUTHORIZED, SUCCESS } from '../constants';
import { useUser } from '../context/UserContext';
import { useNavigate } from 'react-router-dom';

export const LoginForm = () => {
	const navigate = useNavigate()
	const {setUser} = useUser()
    const [password, setPassword] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [error, setError] = React.useState('');

    const logIn = async () => {
        if (!email || !password) {
            setError("Both email and password are required!");
            return
        }

		const response = await loginUser(email, password)

		if (response.status === SUCCESS) {
			setUser(response.data)
			navigate('/home')
		}
		else {
			response.status === CLIENT_ERROR ? setError('Login failed. Please check your credentials.'): setError('Unable to connect to the server. Please try again later.')
		}
    };

    return (
      <div className='background'>
        <div className='container'>
          <h1 className='title'>Fake Stack Overflow</h1>
          <input
            	type="email"
            	placeholder="Enter email"
            	value={email}
            	onChange={(e) => {
              	setEmail(e.target.value);
            	setError('');
            }}
            className={`input-field ${error ? 'error' : ''}`}
          />
          <input
				type="password" 
				placeholder="Enter password"
				value={password}
				onChange={(e) => {
				setPassword(e.target.value);
				setError('');
				}}
            	className={`input-field ${error ? 'error' : ''}`}
          />
          {error && <div className="error-message">{error}</div>}
          <button className='login_page_button' onClick={logIn}>Login</button>
        </div>
      </div>
    );
}
